mapper.select:
SELECT * FROM user_subscribe_news_view
WHERE user_id = #{userId}
ORDER BY id DESC

SELECT * FROM news_and_public_view
WHERE public_id = #{publicId}
ORDER BY id DESC

mapper.update:
UPDATE news
SET click = click + 1
WHERE id = #{id}

UPDATE news
SET title = #{title}, content = #{content}, url_from = #{url}
WHERE id = #{id}

mapper.select:
SELECT COUNT(0)
FROM news_comment
WHERE news_id = #{newsId}
