mapper.update:
UPDATE repair
SET repairer_id = #{repairerId}, `local` = #{local}, content = #{content},
	phone = #{phone}, status = #{status}, resolvetime = #{resolvetime}
WHERE id = #{id}

mapper.select:

WHERE repair.repairer_id = #{repairerId}
ORDER BY repair.id DESC
LIMIT ${((pageNo-1) * pageSize)}, ${pageSize}
